import { css } from 'styled-components';

// todo: timings and easings should be moved into big-design-theme
const Timings = {
  short: '150ms'
};
const Easings = {
  hover: 'ease-out'
};
export const withTransition = function (properties) {
  let duration = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : Timings.short;
  let ease = arguments.length > 2 && arguments[2] !== undefined ? arguments[2] : Easings.hover;
  return css(["transition:all ", " ", ";transition-property:", ";"], duration, ease, properties.join(', '));
};