components/common/facebook-like:
{{#if settings.add_this}}
  {{#each settings.add_this.buttons}}
    {{#if service '===' 'facebook_like'}}
      <div id="fb-root"></div>
      <script>(function(d, s, id) {
        var js, fjs = d.getElementsByTagName(s)[0];
        if (d.getElementById(id)) return;
        js = d.createElement(s); js.id = id;
        js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.5";
        fjs.parentNode.insertBefore(js, fjs);
      }(document, 'script', 'facebook-jssdk'));</script>
    {{/if}}
  {{/each}}
{{/if}}

components/common/main-navigation-pages:
{{#each pages}}
  <li class="main-nav-item nav-page {{#if children}}dropdown{{/if}}">
    <a class="top-level-nav-link pages" href="{{url}}">
      <span>{{name}}</span>
      {{#if children}}
        <span class="dropdown-toggle">
          <svg class="icon icon-arrow-down"><use xlink:href="#icon-arrow-down" /></svg>
        </span>
      {{/if}}
    </a>
    {{#if children}}
      <ul class="dropdown-panel">
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
