components/sitemap/group:
{{#if items.length}}
  <ul>
    {{#each items}}
      <li>
        <a href="{{url}}">{{label}}</a>

        {{#if children}}
          {{> components/sitemap/group items=children}}
        {{/if}}
      </li>
    {{/each}}
    {{#if show_all_link}}
      <li class="sitemap-show-all">
        <a class="link" href="{{subsection_url}}">{{lang 'sitemap.view_all'}}</a>
      </li>
    {{/if}}
  </ul>
{{/if}}
