components/common/main-navigation-pages:
{{#each pages}}
  <li class="main-nav-item nav-page {{#if children}}dropdown{{/if}}">
    <a class="top-level-nav-link pages" href="{{url}}">
      <span>{{name}}</span>
      {{#if children}}
        <span class="dropdown-toggle">
          <svg class="icon icon-arrow-down"><use xlink:href="#icon-arrow-down" /></svg>
        </span>
      {{/if}}
    </a>
    {{#if children}}
      <ul class="dropdown-panel">
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
            {{#if children}}
              <ul>
                {{#each children}}
                  <li><a href="{{url}}">{{name}}</a></li>
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/each}}
      </ul>
    {{/if}}
  </li>
{{/each}}

components/common/mega-nav-variant-child:
{{#each children}}
  {{#if children}}
    <div class="navigation-child-list-container" data-parent-category-id="{{id}}">
      <span class="child-list-back" data-find-parent>
        <svg width="10" height="9" class="icon icon-arrow-down"><use xlink:href="#icon-arrow-down" /></svg>
        <a href="#">Back</a>
      </span>
      <h1 class="navigation-child-list-header">{{name}}</h1>
      <div class="navigation-child-list-inner">
        <ul class="navigation-child-list">
          <li class="navigation-child-item"><a href="{{url}}">{{lang 'common.navigation_all' name=name}}</a></li>
          {{#each children}}
            {{#if children}}
              <li class="navigation-child-item" data-child-category-id="{{id}}" data-find-child>
                <a href="#">{{name}}</a><span class="child-item-arrow"><svg width="10" height="9" class="icon icon-arrow-down"><use xlink:href="#icon-arrow-down" /></svg></span>
              </li>
            {{else}}
              <li class="navigation-child-item"><a href="{{url}}">{{name}}</a></li>
            {{/if}}
          {{/each}}
        </ul>
      </div>
    </div>
    {{> components/common/mega-nav-variant-child-recursive}}
  {{/if}}
{{/each}}

components/common/navigation-mega:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

{{#each nav_items}}
  <li class="parent {{#if children}}has-children{{/if}}">
    <a class="{{#if image}}has-image{{/if}}" href="{{url}}">{{name}}</a>
    {{#unless ../theme_settings.meganav-show-top-level-only}}
      {{#if children}}
      
       <div class="children-menu-image">
          <div class="cat_menu_title"> <a href="{{url}}">{{name}}</a></div>
           
        <ul class="children">
          {{#each children}}
            <li>
              <a class="{{#if image}}has-image{{/if}}" href="{{url}}">{{name}}</a>
              {{#if children}}
                <ul>
                  {{#each children}}
                    <li><a href="{{url}}">{{name}}</a></li>
                  {{/each}}
                </ul>
              {{/if}}
            </li>
          {{/each}}
        </ul>
         <div class="category-image-container-new">
         <div class="category-image-new" style="background-image: url({{getImage image 'product'}});">
           <img class="sr-only" src="{{getImage image 'product'}}" alt="{{image.alt}}" />
           
        </div>
    </div>
        </div>
      {{/if}}
    {{/unless}}
    
   
    
    
    
    
  </li>
{{/each}}

components/header/alert-messages:
{{#each product.reviews.messages}}
  {{#if error}}
    {{> core/alerts/alert-error message=error dismissable=true}}
  {{/if}}
  {{#if success}}
    {{> core/alerts/alert-success message=success dismissable=true}}
  {{/if}}
{{/each}}

{{#if cart.status_messages}}
  {{#each cart.status_messages}}
    {{#if type '==' 0}}
      {{> core/alerts/alert-error message=message dismissable=true}}
    {{/if}}

    {{#if type '==' 1}}
      {{> core/alerts/alert-success message=message dismissable=true}}
    {{/if}}

    {{#if type '==' 3}}
      {{> core/alerts/alert-error message=message dismissable=true}}
    {{/if}}
  {{/each}}
{{/if}}

{{#if shipping_messages}}
  {{#each shipping_messages}}
    {{> core/alerts/alert-info message=message dismissable=true}}
  {{/each}}
{{/if}}

{{#if cart.status_messages}}
  {{#each cart.status_messages}}
    {{#if type '==' 2}}
      {{> core/alerts/alert-info message=message dismissable=true}}
    {{/if}}
  {{/each}}
{{/if}}

{{#if product.shipping_messages}}
  {{#each product.shipping_messages}}
    {{> core/alerts/alert-info message=message dismissable=true}}
  {{/each}}
{{/if}}

components/header/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="currency-selector customer-util" data-currency-selector>
    <span class="form-selected-text" data-currency-selector-toggle>
      {{currency_selector.active_currency_code}}
    </span>
    <i class="icon icon-arrow-down"></i>
    <select class="currencies">
      {{#each currency_selector.currencies}}
        <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}} data-value="{{currency-code}}">
          {{name}}
        </option>
      {{/each}}
    </select>
  </div>
{{/if}}

components/header/customer-utils:
{{> components/header/compare-list}}

{{> components/header/currency-selector}}

{{#if settings.account_creation_enabled}}
  <div class="customer-links customer-util">
    {{> components/header/account-button}}
  </div>
{{/if}}

{{#if settings.show_wishlist}}
  {{#if customer}}
    <a class="top-bar-button button-wishlist customer-util" href="{{urls.account.wishlists.all}}">
      <svg class="icon icon-wishlist"><use xlink:href="#icon-wishlist" /></svg>
    </a>
  {{/if}}
{{/if}}

<div class="mini-cart-wrap dropdown customer-util logo-align-center">
  {{> components/mini-cart/mini-cart }}
</div>
