.emthemesModez-productUniform {
    .card-figure {
        > a {
            max-width: get-width(stencilString("productgallery_size"));
            text-align: center;

            &:after {
                content: "";
                display: block;
                height: 0;
                width: 100%;
                padding-bottom: get-padding(
                    stencilString("productgallery_size")
                );
            }
        }
    }

    .card-image {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        width: auto;
        max-height: 100%;
    }

    .brandGrid {
        .card-figure {
            > a {
                max-width: get-width(stencilString("brand_size"));

                &:after {
                    padding-bottom: get-padding(stencilString("brand_size"));
                }
            }
        }
    }
}

/* =================================================== */
/*                Customization header                 */
/* =================================================== */
.header {
    background: transparent;
    border: 0;
}

.emthemesModez-header-topSection {
    border: 0;
    ul {
        height: 40px;
        li {
            flex: 0 0 auto;
            border: 0;
            text-align: left;
            font-size: remCalc(15);
            color: #fdf9f9;
            font-weight: 400;
            font-style: italic;
            padding: spacing("third") spacing("half");
        }
    }
    .container {
        max-width: 100%;
    }
}

.emthemesModez-header-userSection {
    background: rgba($header-bg, $alpha: 0.95);
}

.emthemesModez-header-userSection {
    padding: 0;
}

.header-logo {
    margin: 0;
    text-align: left;
    padding: 0 spacing("single");
    height: remCalc(80);
    min-width: 20%;
    max-width: 80%;
    flex: 1 1 0;
    > a {
        width: auto;
    }
}

.header-logo-image {
    max-height: remCalc(60);
}

.mobileMenu-toggle {
    left: auto;
    top: remCalc($header-height);
    right: 0;
}

.navPages-container {
    height: calc(100% - 124px);
    border: 0;
    &.is-open {
        padding-top: 0;
    }
}

.navPages {
    background: rgba(78,71,69,0.95);
}

.navPages-list + .navPages-list {
    border-top-color: #d49f72;
}

.navPages-action {
    padding: 0 spacing("third");
    font-size: 0.92rem;
}

.navPages-action svg {
    fill: #d49f72;
    stroke: #d49f72;
}

.navUser--right {
    background: #807973;
    position: static;
    transform: none;
    .navUser-section {
        display: flex;
        align-items: center;
        float: none;
    }
    .navUser-item {
        display: block;
        flex: 0 0 auto;
    }
    .navUser-item--contact {
        flex: 1 1 0;
    }
    .navUser-action {
        padding: remCalc(10) spacing("half");
    }
    .navUser-action-locationLabel, .navUser-item-searchLabel, .navUser-item-cartLabel, .navUser-item-loginLabel {
        display: none;
    }
    .navUser-action--contact {
        font-size: 16px;
        font-weight: 500;
        letter-spacing: 1px;
        display: inline-block;
    }
}

@include breakpoint("medium") {
    body {
        padding-top: remCalc(140);
        margin-top: 0;
        &.page-default {
            padding-top: remCalc(40);
        }
    }
    .header {
        position: fixed;
        left: 0;
        top: 0;
        width: 100%;
    }
    .navPages-container {
        background: transparent;
        position: static;
        height: auto;
        .navPages-item {
            > .navPages-action {
                display: flex;
                align-items: center;
            }
            &:hover {
                > .navPages-action {
                    background-color: stencilColor("navPages-subMenu-backgroundColor");
                }
                > .navPage-subMenu {
                    display: block;
                    width: 100%;
                    left: 0;
                    z-index: zIndex("highest");
                }
            }

        }
    }
    .navPages {
        background: transparent;
    }
    .emthemesModez-header-userSection--logo-left {
        .navPages-container {
            order: 2;
        }
    }
    .navUser--right {
        position: absolute;
        top: 0;
        right: spacing("single");
        .navUser-section {
            height: 40px;
        }
        .navUser-action {
            padding: spacing("third") spacing("half");
        }
        .navUser-item:first-child {
            .navUser-action {
                &:before {
                    display: none;
                }
            }
        }

    }
    .navPage-subMenu-item {
        > .navPage-subMenu-action {
            font-size: .99rem;
            color: #d49f72;
            text-transform: uppercase;
            font-weight: 700;
            line-height: .5;
            margin-bottom: 0;
            border: 0;
            background-color: transparent;
            padding-top: 2rem!important;
        }
        .navPage-childList-action {
            margin-top: spacing("third");
        }
    }
}
@include breakpoint("large") {
    .navPages-container {
        .navPages-item {
            > .navPages-action {
                height: remCalc(100);
            }
        }
    }
    .navUser--right {
        .navUser-action-locationLabel, .navUser-item-searchLabel, .navUser-item-cartLabel, .navUser-item-loginLabel {
            display: inline-block;
        }
    }
    .emthemesModez-header-userSection {
        padding: 0 8%;
    }
    .emthemesModez-header-topSection {
        .container {
            padding: 0 8%;
        }
    }
    .navUser--right {
        right: 8%;
    }
}


@media (max-width:801px) {
    body > div.body > div.container > div > aside {display:none!important;}
}
