    {{else if mega_style '===' '2'}}
        {{> components/common/navigation-list-mega2}}
    {{else if mega_style '===' '3'}}
        {{> components/common/navigation-list-mega3}}
    {{else if mega_style '===' '4'}}
        {{> components/common/navigation-list-mega4}}
    {{else if mega_style '===' '5'}}
        {{> components/common/navigation-list-mega5}}
    {{else if mega_style '===' '6'}}
        {{> components/common/navigation-list-mega6}}
    {{else if mega_style '===' '7'}}
        {{> components/common/navigation-list-mega7}}
    {{else if mega_style '===' '8'}}
        {{> components/common/navigation-list-mega8}}
    {{else if mega_style '===' '9'}}
        {{> components/common/navigation-list-mega9}}
    {{else if mega_style '===' '10'}}
        {{> components/common/navigation-list-mega10}}
    {{/if}}

{{else}}

    {{#if type '===' 'category'}}
        {{> components/common/navigation-list}}
    {{else}}
        {{> components/common/navigation-page-list}}
    {{/if}}

{{/if}}

