components/brand/product-listing-list:
{{#if brand.products}}
  <div class="listing-wrapper">
    <form class="form {{#if show_compare}}compare-enabled{{/if}}" id="form-compare" action="/compare" method='POST' data-product-compare>
      {{#each brand.products}}
        {{> components/products/product-list-item show_rating=../settings.show_product_rating show_compare=../show_compare default_image=../theme_settings.default-product-image}}
      {{/each}}
    </form>
  </div>

  {{> components/common/pagination pagination.brand}}
{{else}}
  <h3 class="listing-no-products">
    {{lang 'category.empty'}}
  </h3>
{{/if}}
