components/common/main-navigation-pages:
{{#each pages}}
  <li class="main-nav-item nav-page {{#if children}}dropdown{{/if}}">
    <a class="top-level-nav-link pages" href="{{url}}">
      <span>{{name}}</span>
      {{#if children}}
        <span class="dropdown-toggle">
          <svg class="icon icon-arrow-down"><use xlink:href="#icon-arrow-down" /></svg>
        </span>
      {{/if}}
    </a>
    {{#if children}}
      <ul class="dropdown-panel">
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
            {{#if children}}
              <ul>
                {{#each children}}
                  <li><a href="{{url}}">{{name}}</a></li>
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/each}}
      </ul>
    {{/if}}
  </li>
{{/each}}

components/common/navigation-mega:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

{{#each nav_items}}
  <li class="parent {{#if children}}has-children{{/if}}">
    <a class="{{#if image}}has-image{{/if}}" href="{{url}}">{{name}}</a>
    {{#unless ../theme_settings.meganav-show-top-level-only}}
      {{#if children}}
      
       <div class="children-menu-image">
          <div class="cat_menu_title"> <a href="{{url}}">{{name}}</a></div>
           
        <ul class="children">
          {{#each children}}
            <li>
              <a class="{{#if image}}has-image{{/if}}" href="{{url}}">{{name}}</a>
              {{#if children}}
                <ul>
                  {{#each children}}
                    <li><a href="{{url}}">{{name}}</a></li>
                  {{/each}}
                </ul>
              {{/if}}
            </li>
          {{/each}}
        </ul>
         <div class="category-image-container-new">
         <div class="category-image-new" style="background-image: url({{getImage image 'product'}});">
           <img class="sr-only" src="{{getImage image 'product'}}" alt="{{image.alt}}" />
           
        </div>
    </div>
        </div>
      {{/if}}
    {{/unless}}
    
   
    
    
    
    
  </li>
{{/each}}

components/header/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="currency-selector customer-util" data-currency-selector>
    <span class="form-selected-text" data-currency-selector-toggle>
      {{currency_selector.active_currency_code}}
    </span>
    <i class="icon icon-arrow-down"></i>
    <select class="currencies">
      {{#each currency_selector.currencies}}
        <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}} data-value="{{currency-code}}">
          {{name}}
        </option>
      {{/each}}
    </select>
  </div>
{{/if}}
