components/brand/sidebar:
{{#if brand.faceted_search_enabled}}
  <span class="link faceted-search-toggle" data-faceted-search-toggle data-show-text="{{lang 'faceted_search.show'}}" data-hide-text="{{lang 'faceted_search.hide'}}">
  </span>
  <div class="faceted-search" data-faceted-search>
    {{#each brand.facets}}
      {{#if visible}}
        {{#if items.length}}
          <div class="facet-list {{display}}" data-facet-filter>

            {{> components/common/facet-toggle title=title}}

            {{{dynamicComponent 'components/faceted-search/facets'}}}

            {{#if remove_url}}
              <a class="link facet-remove" href="{{remove_url}}" data-faceted-search-facet data-facet-remove>{{lang 'faceted_search.clear'}}</a>
            {{/if}}
          </div>
        {{/if}}
        {{#compare facet 'price'}}
          <div class="facet-list {{display}}" data-facet-filter>

            {{> components/common/facet-toggle title=title}}

            {{{dynamicComponent 'components/faceted-search/facets'}}}

            {{#if remove_url}}
              <a class="link facet-remove" href="{{remove_url}}" data-faceted-search-facet data-facet-remove>{{lang 'faceted_search.clear'}}</a>
            {{/if}}
          </div>
        {{/compare}}
      {{/if}}
    {{/each}}
  </div>
{{/if}}

components/common/facet-toggle:
{{!
  @param title
         The title of the faceted list to toggle.
}}

<h5 class="facet-list-title {{#unless start_collapsed}}is-open{{/unless}}" data-facet-toggle>
  {{title}}
  <span class="facet-list-toggle">
    <svg class="icon icon-chevron-down"><use xlink:href="#icon-chevron-down" /></svg>
  </span>
</h5>
